import { configureStore } from "@reduxjs/toolkit";
import weatherReducer from "./slices/weatherSlice"; // Adjust the path if necessary

export const store = configureStore({
  reducer: {
    weather: weatherReducer, // You might add other reducers here as well
  },
});
